import React, { useState, useEffect } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import Table from '@material-ui/core/Table'
import TableBody from '@material-ui/core/TableBody'
import TableCell from '@material-ui/core/TableCell'
import TableContainer from '@material-ui/core/TableContainer'
import TableHead from '@material-ui/core/TableHead'
import TableRow from '@material-ui/core/TableRow'
import Paper from '@material-ui/core/Paper'
import axios from 'axios'
import apirest from '../services/apirest'

const useStyles = makeStyles({
  table: {
    minWidth: 100,
  },
})

function createData(concept, mount, type, date) {
  return { concept, mount, type, date }
}

var dateci = new Date(Date.UTC(2012, 11, 20, 3, 0, 0))
var options = {
  weekday: 'long',
  year: 'numeric',
  month: 'long',
  day: 'numeric',
}

// const fechita = dateci.toLocaleDateString('es-ES', options)
const fecha = new Date()
const fechita =
  fecha.toISOString().split('T')[0] + ' - ' + fecha.toLocaleTimeString()

export default function OperationRecord() {
  const classes = useStyles()
  const [rows, setRows] = useState([])
  const jwt = localStorage.getItem('jwt')
  const user_id = localStorage.getItem('user_id')

  useEffect(async () => {
    let newRows = []
    const res = await axios.get(apirest.apiUrl + '/operations/' + user_id)
    console.log(res)
    res.data.forEach((element) => {
      newRows.push(
        createData(
          // element._id,
          element.concept,
          element.mount,
          element.type,
          element.date,
        ),
      )
    })
    console.log(newRows)
    setRows(newRows)
  }, [])

  return (
    <TableContainer component={Paper}>
      <Table className={classes.table} size="small" aria-label="a dense table">
        <TableHead>
          <TableRow>
            <TableCell>Concepto</TableCell>
            <TableCell align="right">Monto</TableCell>
            <TableCell align="right">Tipo</TableCell>
            <TableCell align="right">Fecha y hora</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <TableRow key={row.name}>
              <TableCell component="th" scope="row">
                {row.concept}
              </TableCell>
              <TableCell align="right">{row.mount}</TableCell>
              <TableCell align="right">{row.type}</TableCell>
              <TableCell align="right">{row.date}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  )
}
