import React from 'react'
import ReactDOM from 'react-dom'
import { makeStyles } from '@material-ui/core/styles'
import Table from '@material-ui/core/Table'
import TableBody from '@material-ui/core/TableBody'
import TableCell from '@material-ui/core/TableCell'
import TableHead from '@material-ui/core/TableHead'
import TableRow from '@material-ui/core/TableRow'
import Input from '@material-ui/core/Input'
import Paper from '@material-ui/core/Paper'
import IconButton from '@material-ui/core/IconButton'
// Icons
import EditIcon from '@material-ui/icons/EditOutlined'
import DoneIcon from '@material-ui/icons/DoneAllTwoTone'
import DeleteIcon from '@material-ui/icons/Delete'

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    marginTop: theme.spacing(3),
    overflowX: 'auto',
  },
  table: {
    minWidth: 650,
  },
  selectTableCell: {
    width: 60,
  },
  tableCell: {
    width: 130,
    height: 40,
  },
  input: {
    width: 130,
    height: 40,
  },
}))

const createData = (id, concept, mount, type, date) => ({
  id,
  concept,
  mount,
  type,
  date,
  isEditMode: false,
})

const CustomTableCell = ({ row, name, onChange }) => {
  const classes = useStyles()
  const { isEditMode } = row
  return (
    <TableCell align="left" className={classes.tableCell}>
      {isEditMode && name != 'type' ? (
        <Input
          value={row[name]}
          name={name}
          onChange={(e) => onChange(e, row)}
          className={classes.input}
        />
      ) : (
        row[name]
      )}
    </TableCell>
  )
}
const fecha = new Date()
const fechita =
  fecha.toISOString().split('T')[0] + ' - ' + fecha.toLocaleTimeString()
function EditableTable() {
  const [rows, setRows] = React.useState([
    createData(1, 'Alquiler', 2000, 1, fechita),
    createData(2, 'PrÃ©stamo', 3000, 1, fechita),
    createData(3, 'Ventas', 1500.5, 1, fechita),
    createData(4, 'Alquiler', 1894.35, 1, fechita),
  ])
  const [previous, setPrevious] = React.useState(rows)
  const classes = useStyles()

  const onToggleEditMode = (id) => {
    setRows((state) => {
      return rows.map((row) => {
        if (row.id === id) {
          return { ...row, isEditMode: !row.isEditMode }
        }
        return row
      })
    })
  }

  const onChange = (e, row) => {
    if (!previous[row.id]) {
      setPrevious((state) => ({ ...state, [row.id]: row }))
    }
    const value = e.target.value
    const name = e.target.name
    const { id } = row
    const newRows = rows.map((row) => {
      if (row.id === id) {
        return { ...row, [name]: value }
      }
      return row
    })
    setRows(newRows)
  }

  const handleDelete = (rowID) => {
    console.log(rows)
    const newData = new Array()
    rows.map((row) => {
      if (row.id != rowID) {
        newData.push(row)
      }
    })
    setRows(newData)
  }

  return (
    <Paper className={classes.root}>
      <Table className={classes.table} aria-label="caption table">
        <caption>A barbone structure table example with a caption</caption>
        <TableHead>
          <TableRow>
            <TableCell align="left" />
            <TableCell align="left">Dessert (100g serving)</TableCell>
            <TableCell align="left">mount</TableCell>
            <TableCell align="left">type&nbsp;(g)</TableCell>
            <TableCell align="left">date&nbsp;(g)</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <TableRow key={row.id}>
              <TableCell className={classes.selectTableCell}>
                {row.isEditMode ? (
                  <>
                    <IconButton
                      aria-label="done"
                      onClick={() => onToggleEditMode(row.id)}
                    >
                      <DoneIcon />
                    </IconButton>
                    <IconButton
                      aria-label="revert"
                      onClick={() => handleDelete(row.id)}
                    >
                      <DeleteIcon />
                    </IconButton>
                  </>
                ) : (
                  <IconButton
                    aria-label="delete"
                    onClick={() => onToggleEditMode(row.id)}
                  >
                    <EditIcon />
                  </IconButton>
                )}
              </TableCell>
              <CustomTableCell {...{ row, name: 'name', onChange }} />
              <CustomTableCell {...{ row, name: 'mount', onChange }} />
              <CustomTableCell {...{ row, name: 'type', onChange }} />
              <CustomTableCell {...{ row, name: 'date', onChange }} />
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </Paper>
  )
}

export default EditableTable
