import React, { useState } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import TextField from '@material-ui/core/TextField'
import Grid from '@material-ui/core/Grid'
import Button from '@material-ui/core/Button'
import InputIcon from '@material-ui/icons/Input'
import InputLabel from '@material-ui/core/InputLabel'
import FormControl from '@material-ui/core/FormControl'
import Select from '@material-ui/core/Select'
import axios from 'axios'
import apirest from '../services/apirest'
import { Redirect } from 'react-router-dom'

const useStyles = makeStyles((theme) => ({
  root: {},
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}))

function OperationForm() {
  const classes = useStyles()
  const jwt = localStorage.getItem('jwt')
  const user_id = localStorage.getItem('user_id')
  const [state, setState] = React.useState({
    operationType: 1,
    mount: 0,
    concept: '',
  })
  const [redirect, setRedirect] = useState(false)

  const handleChange = (event) => {
    const name = event.target.name
    setState({
      ...state,
      [name]: event.target.value,
    })
    console.log(event.target.value)
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    const operation = {
      user_id: user_id,
      concept: state.concept,
      mount: state.mount,
      type: state.operationType,
      date: '2020-06-12T00:00:00.000Z',
    }
    console.log('enviando: ', operation)
    axios
      .post(apirest.apiUrl + '/operations', operation)
      .then(setRedirect(true))
  }
  if (redirect) {
    return <Redirect to="/operations" />
  }
  return (
    <form className={classes.root} autoComplete="off" onSubmit={handleSubmit}>
      <Grid container spacing={4}>
        <Grid item xs={12} md={6}>
          <TextField
            error={false}
            helperText="Ingrese un número decimal. Centavos opcionales."
            id="mount"
            label="Monto $"
            name="mount"
            fullWidth={true}
            onChange={handleChange}
          />
        </Grid>
        <Grid item xs={12} md={6}>
          <FormControl className={classes.formControl}>
            <InputLabel htmlFor="operationType">Tipo</InputLabel>
            <Select
              native
              value={state.operationType}
              onChange={handleChange}
              style={{ minWidth: 600 }}
              inputProps={{
                name: 'operationType',
                id: 'operationType',
              }}
            >
              <option value={1}>Ingreso</option>
              <option value={2}>Egreso</option>
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs={12} md={6}>
          <TextField
            error={false}
            id="concept"
            label="Concepto"
            name="concept"
            fullWidth={true}
            onChange={handleChange}
          />
        </Grid>
        <Grid item xs={12} sm={12} md={12}>
          <Button
            type="submit"
            variant="contained"
            color="primary"
            startIcon={<InputIcon />}
            fullWidth={true}
          >
            Realizar operación
          </Button>
        </Grid>
      </Grid>
    </form>
  )
}

export default OperationForm
