import React from 'react'
import { makeStyles, Container, Typography } from '@material-ui/core'
import TextField from '@material-ui/core/TextField'
import Button from '@material-ui/core/Button'
import { useForm, Controller } from 'react-hook-form'
import NavbarUnregistered from '../components/NavbarUnregistered'

const useStyles = makeStyles((theme) => ({
  paper: {
    marginTop: theme.spacing(11),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  form: {
    width: '100%', // Fix IE 11 issue.
  },
  field: {
    marginTop: '1.2em',
  },
}))

const SignUp = ({ handleClose }) => {
  const classes = useStyles()
  const { handleSubmit, control } = useForm()

  const onSubmit = (data) => {
    console.log(data)
  }

  return (
    <>
      <NavbarUnregistered />
      <Container component="main" maxWidth="xs" className={classes.paper}>
        <Typography component="h1" variant="h5">
          Formulario de registro
        </Typography>
        <form
          className={classes.form}
          noValidate
          onSubmit={handleSubmit(onSubmit)}
        >
          <Controller
            name="name"
            control={control}
            render={({ field: { onChange, value }, fieldState: { error } }) => (
              <TextField
                variant="outlined"
                margin="normal"
                fullWidth
                id="name"
                label="Nombre y apellido"
                autoFocus
                onChange={onChange}
                error={!!error}
                helperText={
                  error
                    ? 'Este campo es obligatorio. Cuatro caracteres como mínimo.'
                    : ''
                }
                className={classes.field}
              />
            )}
            rules={{ required: 'Este campo es obligatorio', minLength: 4 }}
          />
          <Controller
            name="email"
            control={control}
            defaultValue=""
            render={({ field: { onChange, value }, fieldState: { error } }) => (
              <TextField
                label="Email"
                variant="outlined"
                fullWidth
                value={value}
                onChange={onChange}
                error={!!error}
                helperText={error ? 'Debe ingresar un email válido.' : ''}
                type="email"
                className={classes.field}
              />
            )}
            rules={{
              required: 'Este campo es obligatorio.',
              pattern: /^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/,
            }}
            fullWidth
          />
          <Controller
            name="password"
            control={control}
            defaultValue=""
            render={({ field: { onChange, value }, fieldState: { error } }) => (
              <TextField
                label="Password"
                variant="outlined"
                value={value}
                onChange={onChange}
                error={!!error}
                helperText={
                  error ? 'Debe ingresar una contraseña de 4 a 16 dígitos.' : ''
                }
                type="password"
                className={classes.field}
              />
            )}
            rules={{
              required: 'Campo obligatorio',
              minLength: 4,
              maxLength: 16,
            }}
          />
          <div>
            <Button variant="contained" onClick={handleClose}>
              Cancel
            </Button>
            <Button type="submit" variant="contained" color="primary">
              Signup
            </Button>
            {/* <Typography color="secondary">{errorMsg}</Typography> */}
          </div>
        </form>
      </Container>
    </>
  )
}

export default SignUp
