import React from 'react'
import ReactDOM from 'react-dom'
import { makeStyles } from '@material-ui/core/styles'
import Table from '@material-ui/core/Table'
import TableBody from '@material-ui/core/TableBody'
import TableCell from '@material-ui/core/TableCell'
import TableHead from '@material-ui/core/TableHead'
import TableRow from '@material-ui/core/TableRow'
import Input from '@material-ui/core/Input'
import Paper from '@material-ui/core/Paper'
import IconButton from '@material-ui/core/IconButton'
// Icons
import EditIcon from '@material-ui/icons/EditOutlined'
import DoneIcon from '@material-ui/icons/DoneAllTwoTone'
import DeleteIcon from '@material-ui/icons/Delete'
import { useState, useEffect } from 'react'
import axios from 'axios'
import apirest from '../services/apirest'

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    marginTop: theme.spacing(3),
    overflowX: 'auto',
  },
  table: {
    minWidth: 650,
  },
  selectTableCell: {
    width: 60,
  },
  tableCell: {
    width: 130,
    height: 40,
  },
  input: {
    width: 130,
    height: 40,
  },
}))

const createData = (id, concept, mount, type, date) => ({
  id,
  concept,
  mount,
  type,
  date,
  isEditMode: false,
})

const CustomTableCell = ({ row, name, onChange }) => {
  const classes = useStyles()
  const { isEditMode } = row
  return (
    <TableCell align="left" className={classes.tableCell}>
      {isEditMode && name != 'type' ? (
        <Input
          value={row[name]}
          name={name}
          onChange={(e) => onChange(e, row)}
          className={classes.input}
        />
      ) : (
        row[name]
      )}
    </TableCell>
  )
}
function EditableTable() {
  const classes = useStyles()
  const [rows, setRows] = useState([])
  const [previous, setPrevious] = React.useState(rows)
  const jwt = localStorage.getItem('jwt')
  const user_id = localStorage.getItem('user_id')
  useEffect(async () => {
    let newRows = []
    const res = await axios.get(apirest.apiUrl + '/operations/' + user_id)
    res.data.forEach((element) => {
      newRows.push(
        createData(
          element._id,
          element.concept,
          element.mount,
          element.type,
          element.date,
        ),
      )
    })
    console.log(newRows)
    setRows(newRows)
  }, [])

  const onToggleEditMode = (id) => {
    setRows((state) => {
      return rows.map((row) => {
        if (row.id === id) {
          return { ...row, isEditMode: !row.isEditMode }
        }
        return row
      })
    })
  }

  const onChange = (e, row) => {
    if (!previous[row.id]) {
      setPrevious((state) => ({ ...state, [row.id]: row }))
    }
    const value = e.target.value
    const name = e.target.name
    const { id } = row
    const newRows = rows.map((row) => {
      if (row.id === id) {
        return { ...row, [name]: value }
      }
      return row
    })
    setRows(newRows)
  }

  const handleDelete = (rowID) => {
    console.log(rows)
    const newData = new Array()
    rows.map((row) => {
      if (row.id != rowID) {
        newData.push(row)
      }
    })
    setRows(newData)
  }

  return (
    <Paper className={classes.root}>
      <Table className={classes.table} aria-label="caption table">
        <caption>
          Utilice el botón de lápiz para editar o eliminar un registro.
        </caption>
        <TableHead>
          <TableRow>
            <TableCell align="left" />
            <TableCell align="left">Concepto</TableCell>
            <TableCell align="left">Importe</TableCell>
            <TableCell align="left">Tipo</TableCell>
            <TableCell align="left">Fecha y hora</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map((row) => (
            <TableRow key={row.id}>
              <TableCell className={classes.selectTableCell}>
                {row.isEditMode ? (
                  <>
                    <IconButton
                      aria-label="done"
                      onClick={() => onToggleEditMode(row.id)}
                    >
                      <DoneIcon />
                    </IconButton>
                    <IconButton
                      aria-label="revert"
                      onClick={() => handleDelete(row.id)}
                    >
                      <DeleteIcon />
                    </IconButton>
                  </>
                ) : (
                  <IconButton
                    aria-label="delete"
                    onClick={() => onToggleEditMode(row.id)}
                  >
                    <EditIcon />
                  </IconButton>
                )}
                edit
              </TableCell>
              <CustomTableCell {...{ row, name: 'concept', onChange }} />
              <CustomTableCell {...{ row, name: 'mount', onChange }} />
              <CustomTableCell {...{ row, name: 'type', onChange }} />
              <CustomTableCell {...{ row, name: 'date', onChange }} />
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </Paper>
  )
}

export default EditableTable
